package com.kraftlabs.crm_new.LocationTracker;import android.content.BroadcastReceiver;import android.content.Context;import android.content.Intent;import android.util.Log;/** * User: Ashik * Date: 28-Jul-18 * Time: 3:35 PM */public class ServiceRestarterBroadcastReceiver extends BroadcastReceiver {    private static final String TAG = ServiceRestarterBroadcastReceiver.class.getSimpleName();    @Override    public void onReceive(Context context, Intent intent) {        Log.i(TAG, "onReceive: ooh...service stopped");        context.startService(new Intent(context, UpdateLocation.class));    }}